select.if:
SELECT `status`, count(1) value , 1 type,
FORMAT(IF ( ( SELECT count(1) FROM stock_storage WHERE del_flag = 0
) != 0, count(1) / ( SELECT count(1) FROM stock_storage WHERE del_flag = 0
) * 100, 0 ) ,2) rate FROM stock_storage WHERE del_flag = 0
GROUP BY `status`

SELECT `status`, count(1) value, 1 type,
FORMAT(IF ( ( SELECT count(1) FROM stock_takeout WHERE del_flag = 0
) != 0, count(1) / ( SELECT count(1) FROM stock_takeout WHERE del_flag = 0
) * 100, 0 ) ,2) rate FROM stock_takeout WHERE del_flag = 0 GROUP BY `status`

mapper.select:
SELECT client_short_name clientName, return_time date, IFNULL(r.volume,0) returnVolume, IFNULL(r.weight,0) returnWeight, IFNULL(r.number,0) returnNumber, IFNULL(r.total,0) returnTotal , IFNULL(t.volume,0) takeoutVolume, IFNULL(t.weight,0) takeoutWeight, IFNULL(t.number,0) takeoutNumber, IFNULL(t.total,0) takeoutTotal , IFNULL(s.volume,0) storageVolume, IFNULL(s.weight,0) storageWeight, IFNULL(s.number,0) storageNumber, IFNULL( s.total,0) storageTotal FROM (SELECT id ,client_short_name from client_basicdata where del_flag =0
and id = #{clientId}
) client LEFT JOIN ( SELECT client_id, return_time, SUM(volume) volume, SUM(weight) weight, SUM(number) number, SUM(total) total FROM stock_sale_return WHERE del_flag = 0
AND `status` > 1 GROUP BY client_id ) r on client.id = r.client_id LEFT JOIN ( SELECT client_id, takeout_time, SUM(volume) volume, SUM(weight) weight, SUM(number) number, SUM(total) total FROM stock_takeout WHERE del_flag = 0
AND `status` > 1 GROUP BY client_id ) t ON r.client_id = t.client_id LEFT JOIN ( SELECT client_id, storage_time, SUM(volume) volume, SUM(weight) weight, SUM(number) number, SUM(total) total FROM stock_storage WHERE del_flag = 0
AND `status` > 1 GROUP BY client_id ) s ON r.client_id = s.client_id
LIMIT #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

select ifnull(count(1),0) from ( SELECT client_short_name clientName, return_time date, IFNULL(r.volume,0) returnVolume, IFNULL(r.weight,0) returnWeight, IFNULL(r.number,0) returnNumber, IFNULL(r.total,0) returnTotal , IFNULL(t.volume,0) takeoutVolume, IFNULL(t.weight,0) takeoutWeight, IFNULL(t.number,0) takeoutNumber, IFNULL(t.total,0) takeoutTotal , IFNULL(s.volume,0) storageVolume, IFNULL(s.weight,0) storageWeight, IFNULL(s.number,0) storageNumber, IFNULL( s.total,0) storageTotal FROM (SELECT id ,client_short_name from client_basicdata where del_flag =0
and id = #{clientId}
) client LEFT JOIN ( SELECT client_id, return_time, SUM(volume) volume, SUM(weight) weight, SUM(number) number, SUM(total) total FROM stock_sale_return WHERE del_flag = 0
AND `status` > 1 GROUP BY client_id ) r on client.id = r.client_id LEFT JOIN ( SELECT client_id, takeout_time, SUM(volume) volume, SUM(weight) weight, SUM(number) number, SUM(total) total FROM stock_takeout WHERE del_flag = 0
AND `status` > 1 GROUP BY client_id ) t ON r.client_id = t.client_id LEFT JOIN ( SELECT client_id, storage_time, SUM(volume) volume, SUM(weight) weight, SUM(number) number, SUM(total) total FROM stock_storage WHERE del_flag = 0
AND `status` > 1 GROUP BY client_id ) s ON r.client_id = s.client_id ) temp
