SELECT d.id, m.id as materialId, m.client_id AS clientId, m.tray, m.type, m.item_id AS itemId, m.batch_number AS batchNumber, m.lock_code AS lockCode, d.whole_num AS wholeNum, d.scattered_num AS scatteredNum, d.depot_id as depot, d.number AS availableNum, m.material_status AS materialStatus, m.create_by AS createId, m.create_date AS createDate, m.update_by AS updateId, m.update_date AS updateDate, m.del_flag AS delFlag, m.remarks FROM stock_material_depot d LEFT JOIN stock_material m ON d.material_id = m.id WHERE d.number != 0
and d.depot_id = #{depotCode,jdbcType=VARCHAR}
and d.id = #{depotMaterilId,jdbcType=VARCHAR}
LIMIT #{start,jdbcType=INTEGER},#{limit,jdbcType=INTEGER}

select count(1) from ( SELECT d.id, m.client_id AS clientId, m.depot, m.tray, m.item_id AS itemId, m.batch_number AS batchNumber, d.depot_id AS depotCode, m.lock_code AS lockCode, d.number AS availableNum, m.material_status AS materialStatus, m.create_by AS createId, m.create_date AS createDate, m.update_by AS updateId, m.update_date AS updateDate, m.del_flag AS delFlag, m.remarks FROM stock_material_depot d LEFT JOIN stock_material m ON d.material_id = m.id WHERE d.number != 0
and d.depot_id = #{depotCode,jdbcType=VARCHAR}
) temp

SELECT * FROM stock_material WHERE id IN ( SELECT DISTINCT material_id FROM stock_material_operations o LEFT JOIN stock_material m ON o.material_id = m.id WHERE 1=1
and m.client_id = #{clientId,jdbcType=VARCHAR}
and o.create_date between #{startTime} and #{endTime}
)
